```
// This is what I have so far.  It  might not be what you're looking for but
// this was for the npm script part.
// Line 33, while anchors is where I was at before I had to stop.  It needs to
// be rewritten probably or at least completed.

const fs = require( 'fs' );
const path = require( 'path' );
const readdirp = require( 'readdirp' );
const glob = require( 'glob' );


// Glob file filter
glob( path.resolve( __dirname, './common/components/**/*.{ts,tsx}' ), function( er, files ) {
  files.forEach( function( filename ) {
    fs.readFile( filename, 'utf-8', function( err, content ) {
      inspectFile( filename, content );
    });
  });
});


function inspectFile( filename, content ) {
  const pattern = new RegExp( /<a([A-Za-z0-9 _]*)([^<]+)>/, 'g' );

  let anchors;

  return new Promise( ( resolve, reject ) => {
    fs.readFile( filename, function ( err, fileData ) {
      if ( err ) {
        console.log( err );
        return resolve( content );
      }

      while ( ( anchors = pattern.exec( fileData ) ) !== null ) {
        if ( anchors[0].getAttribute( 'target' ) === '_blank' && ~anchors[0].getAttribute( 'rel' ) ) {
          anchors[0].setAttribute( 'rel', 'noopener noreferrer' );
        }

        if ( ~anchors[0].getAttribute( 'href' ).indexOf( 'http://' ) ) {
          let secure = anchors[0].href.split( '/' );

          anchors[0].href = `https://${secure[2]}`;
        }
      }
    });
  });
```

