// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Example {
    struct User {
        string name;
        uint256 age;
    }

    mapping (address => User) public userByAddress;

    function setUser(User memory user) public {
        userByAddress[msg.sender] = user;
    }
}
