contract MyInitialScript is Script, TestingSamples, EIP712Upgradeable {
    using ECDSAUpgradeable for *;
    function run() external {
        uint256 deployerPrivateKey = vm.envUint("ANVIL_PRIVATE_KEY");
        vm.startBroadcast(deployerPrivateKey);
        ...instanciating all the dependencies...

        ERC1967Proxy registryProxy = new ERC1967Proxy(address(registryTemplate), abi.encodeWithSelector(
            Registry(address(0)).initialize.selector,
            addresses_for_dependencies
            )
        );
        console.log("Registry Proxy deployed to: ", address(registryProxy));
        RegistryLibrary.TempOrgProxy memory organizerStruct = RegistryLibrary.TempOrgProxy({organizationName:TestingSamples.stringOrganizerName,
           creator:msg.sender}); 

       
        bytes32 beforeDigest = keccak256(abi.encode(RegistryLibrary.ORGPROXY_TYPEHASH,keccak256(abi.encodePacked(organizerStruct.organizationName)),organizerStruct.creator));
        
        bytes32 messageHash = ECDSAUpgradeable.toTypedDataHash(domainSeparatorRegistry, beforeDigest);
        (uint8 v, bytes32 r, bytes32 s) = vm.sign(TestingSamples.eoaPrivateKey, messageHash);    
        bytes memory bytesSignature = abi.encodePacked(r, s, v);
        address preComputedOrganizer = Registry(address(registryProxy)).preComputeOrgProxy(TestingSamples.organizerName);
        address organizerProxy = Registry(address(registryProxy)).createOrganizerProxy(organizerStruct, bytesSignature);

        runEventProxy(address(organizerProxy));

        vm.stopBroadcast();   
        }

        function runEventProxy(address _organizerProxy) internal {

        string memory creatorString =  Organizer(address(_organizerProxy)).getOrganizationName();
        OrganizerLibrary.TempEventProxy memory eventStruct = OrganizerLibrary.TempEventProxy({seriesName:TestingSamples.stringEventSeriesName,
           creator:creatorString,
           numberOfSubEvents:TestingSamples.numberOfSubEvents
           }); 

        bytes32 beforeDigestEvent = keccak256(abi.encode(OrganizerLibrary.EVENTPROXY_TYPEHASH,keccak256(abi.encodePacked(eventStruct.seriesName)),
        keccak256(abi.encodePacked(eventStruct.creator)),eventStruct.numberOfSubEvents));

        bytes32 messageEventHash = ECDSAUpgradeable.toTypedDataHash(domainSeparatorOrganizer, beforeDigestEvent);

        (uint8 v2, bytes32 r2, bytes32 s2) = vm.sign(TestingSamples.eoaPrivateKey, messageEventHash);          
        bytes memory bytesSignatureEvent = abi.encodePacked(r2, s2, v2);
        address preComputedEvent = Organizer(_organizerProxy).preComputeEvProxy(TestingSamples.eventSeriesName, TestingSamples.numberOfSubEvents);

        address eventProxy = Organizer(_organizerProxy).createEventProxy(eventStruct, bytesSignatureEvent);
        console.log(
            "Current code");     
    }

    // Write addresses to the fs
    function writeAddress(address _address, string memory nameOfContract) public {
        string memory path = "./deployments/initialDeployments.ts";
        
        string memory line = string.concat('export const ',nameOfContract,' = \"',StringsUpgradeable.toHexString(_address),'\";');
        vm.writeLine(path, line);

    }
}
