             :           handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f                          :           handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f
             :   block.timestamp:86401                                                               :   block.timestamp:86401
             :      block.number:1                                                                   :      block.number:1
             : handler.t.counter:0                                                                   : handler.t.counter:0
             :       handler.t.a:0                                                                   :       handler.t.a:0
             :       handler.t.b:0                                                                   :       handler.t.b:0
             :   handler.counter:0                                                                   :   handler.counter:0
             :         handler.a:0                                                                   :         handler.a:0
             :         handler.b:0                                                                   :         handler.b:0
invariant_A():                                                                          invariant_A():
             :             handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f                        :             handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72
             :     block.timestamp:1                                                                 :     block.timestamp:86401
             :        block.number:1                                                                 :        block.number:1
             :             counter:0                                                                 :             counter:0
             : handler.t().counter:0                                                                 : handler.t().counter:0
             :       handler.t().a:0                                                                 :       handler.t().a:0
             :       handler.t().b:0                                                                 :       handler.t().b:0
             :     handler.counter:0                                                                 :     handler.counter:0
             :           handler.a:0                                                                 :           handler.a:0
             :           handler.b:0                                                                 :           handler.b:0
incrementA()                                                                            incrementA()
             :           handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f                          :           handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f
             :   block.timestamp:1                                                                   :   block.timestamp:86401
             :      block.number:1                                                                   :      block.number:1
             : handler.t.counter:1                                                                   : handler.t.counter:1
             :       handler.t.a:1                                                                   :       handler.t.a:1
             :       handler.t.b:0                                                                   :       handler.t.b:0
             :         handler.a:1                                                                   :         handler.a:1
             :   handler.counter:1                                                                   :   handler.counter:1
             :         handler.a:1                                                                   :         handler.a:1
             :         handler.b:0                                                                   :         handler.b:0
invariant_A():                                                                          invariant_A():
             :             handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f                        :             handler:0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f
             :     block.timestamp:1                                                                 :     block.timestamp:86401
             :        block.number:1                                                                 :        block.number:1
             :             counter:0                                                                 :             counter:0
             : handler.t().counter:1                                                                 : handler.t().counter:1
             :       handler.t().a:1                                                                 :       handler.t().a:1
             :       handler.t().b:0                                                                 :       handler.t().b:0
             :     handler.counter:1                                                                 :     handler.counter:1
             :           handler.a:1                                                                 :           handler.a:1
             :           handler.b:0                                                                 :           handler.b:0
