## Issue
callback in for call() myMethod function call doesn't work when for a truffle contract in the test-bed.
What the issue is, in broad strokes.

## Steps to Reproduce
set up truffle with with http://localhost:8545/, 10 accounts
Please provide the shortest amount of steps to reproduce your issue.
```
pragma solidity >=0.4.22 <0.6.0;
pragma experimental ABIEncoderV2;

contract permission {
    /* Define variable owner of the type address */
   enum Status {pending,rejected,accepted}

    struct transaction {
        address payable sender;
        address receiver;
        string datatype;
        Status status;
    }
    
    modifier condition(bool _condition) {
        require(_condition);
        _;
    }
    
    modifier onlyUser(address _address){
        require(_address == msg.sender,"only user transactions");
        _;
    }
    
    transaction[] transactions;
    transaction[] temp;
    function newTransact(address _address,string memory _datatype) public{
        transaction memory newTransaction = transaction({sender:msg.sender,receiver:_address,datatype:_datatype,status:Status.pending});
        transactions.push(newTransaction);
    }
    
    /* This constructor is executed at initialization and sets the owner of the contract */
    
    event Updated(Status _status);
    
    function findTransactions(address _address) public returns(transaction[] memory,uint256 ) {
        transaction memory tmp;
        uint256 i=0;
        uint256 j = 0;
        for(i =0; i<transactions.length; i++){
            if((transactions[i].receiver == _address) && (transactions[i].status == Status.pending)){
                tmp = transactions[i];
                temp.push(tmp);
                j = j + 1;
            }
        }    
        return(temp,j);
    }
    function findTransaction(address _sender,address _address,uint256 _data) public onlyUser(_address) {
        uint256 i;
        for(i =0; i<transactions.length; i++){
            if((transactions[i].sender == _sender) && (transactions[i].receiver == _address) && (transactions[i].status == Status.pending)){
                if (_data == 2){
                    transactions[i].status = Status.accepted;
                }
                else if (_data == 1){
                    transactions[i].status = Status.rejected;
                }
                emit Updated(transactions[i].status);
                break;
            }
        }    
    }
   
     function findTransactionsCount(address _address) public view returns(uint256 cnt) {
        uint256 j = 0;
        uint256 length = transactions.length;
        for(uint256 i =0; i<length; i++){
            if((transactions[i].receiver == _address) && (transactions[i].status == Status.pending)){
                j= j +1;
            }
        }    
        return(j);
    }

    function getTransactions() public view returns(transaction[] memory) {
        return(transactions);
    }
    function getTransactionCount() public view returns(uint256 cnt) {
        uint256 len = transactions.length;
        return(len);
    }
}   
 ```
