            "    at /Users/amal/work/truffle/node_modules/@gnd/graphql-tools/src/stitching/delegateToSchema.ts:104:12",
            "    at step (/Users/amal/work/truffle/node_modules/@gnd/graphql-tools/dist/stitching/delegateToSchema.js:31:23)",
