            "id": 5,
            "mutability": "constant",
            "name": "c",
            "nodeType": "VariableDeclaration",
            "scope": 6,
            "src": "35:22:0",
            "typeDescriptions": {
              "typeIdentifier": "t_uint256",
              "typeString": "uint256"
            },
            "typeName": {
